import "./index.css";
import {useState, useEffect} from "react"
import service from "./service"
const App = () => {
  const [quotes, setQuotes]=useState("")
  const [newQuote, setNewQuote]=useState("")

  useEffect(() => {
    service.getAll().then(initialData => {
      setQuotes(initialData)
    })
  })

  const addQuote = event => {
    event.preventDefault()
    const quoteObject = {
      quote: newQuote
    }

    service
      .create(quoteObject)
      .then(newQuote => {
        setQuotes(quotes.concat(newQuote))
      })

    setNewQuote("")
  }

  const handleQuoteChange = event => {
    setNewQuote(event.target.value)
  }
  console.log({quotes})
  return (
    <div className="container">
      <center>
        <h1>Quote of the day</h1>
        <p>"{quotes[0]}"</p>
        <form className="jari" onSubmit={addQuote}>
          <center>
            <input className="kari"value={newQuote} onChange={handleQuoteChange}/>
          </center>
          <div></div>
          <button className="juki"type="submit">add your thoughts</button>
        </form>
      </center>
    </div>
  )
}
export default App
